<script>
export default {
  data() {
    return {
      lang: '',
      token: '',
      isLogin: false,
      screenWidth: null,
      pcUserShow: false,
      mobileUserShow: false,
      mobileMenuShow: false,
      langCurrencyShow: false,
      mobileLangCurrencyShow: false
    }
  },
  mounted() {
    this.screenWidth = document.body.clientWidth
    console.log(this.screenWidth)
    console.log('获取token')
    this.lang = this.$i18n.locale
    this.token = localStorage.getItem('token')
    if (this.token) {
      this.isLogin = true
    }
    console.log(this.lang)
    console.log(this.token);
  },
  methods: {
    changeLang(lang) {
      this.lang = lang
      this.$i18n.locale = lang
      localStorage.setItem('lang', lang)
      this.langCurrencyShow = false
      this.mobileLangCurrencyShow = false
    },
    switchMobileMenu(e) {
      let id = e.currentTarget.id
      let dom = "div[aria-labelledby='" + id + "']"
      let childDom = document.querySelector(dom)
      if ('none' === childDom.style.display) {
        childDom.style.display = 'block'
        document.getElementById(id).children[1].classList.add('is-active');
      } else {
        childDom.style.display = 'none'
        document.getElementById(id).children[1].classList.remove('is-active');
      }
    }
  }
}
</script>

